<?phpglobal $wpdb;$ap_fecth=$wpdb->get_results("select * from $wpdb->prefix"."apt_appointments");$num_rows=count($ap_fecth);	if($num_rows !==0) {		foreach($ap_fecth as $value) {			$id=$value->id;			$client_email=$value->client_email;			$client_name=$value->client_name;			$staff_member=$value->staff_member;			$ap_fecth_staff=$wpdb->get_results("select * from $wpdb->prefix"."apt_staff where id='$staff_member'");			foreach($ap_fecth_staff as $ap_fecth_single_staff) {				$staff_name= $ap_fecth_single_staff->staff_member_name;				$staff_email= $ap_fecth_single_staff->staff_email;			}			$service_type=$value->service_type;			$contact =$value->contact ;			$booking_date=$value->booking_date ;			$start_time =$value->start_time ;			$end_time=$value->end_time;			$status=$value->status;			$results["data"][] =array('<input type="checkbox" name="check[]" id="appoint_check" class="checkbox1" value="'.$id.'"/>',$id,array($booking_date,$start_time,"To",$end_time),$client_name,$service_type,			'<select size="1" class="payment_status_value" name="payment_status1" apt_client_name="'.$client_name.'" apt_date="'.$booking_date.'" apt_time="'.$start_time.'" apt_end_time="'.$end_time.'" apt_service_name="'.$service_type.'" apt_staff_name="'.$staff_name.'"  apt_staff_email="'.$staff_email.'"  title="'.$client_email.'" data-id="'.$id.'">				<option value="pending"'.(($status == 'pending')?'selected':"").'>Pending</option>				<option value="approved"'.(($status == 'approved')?'selected':"").'>Approved</option>				<option value="cancel" '.(($status == 'cancel')?'selected':"").'>Cancel</option>				<option value="completed" '.(($status == 'completed')?'selected':"").'>Completed</option>			</select>',			$contact,array('<a style="margin-right: 5px;" href="#update_appoin_model"  data-backdrop="true" data-toggle="modal" data-id="'.$id.'"><i class="glyphicon glyphicon-pencil"></i></a> &nbsp <a href="#'.$id.'" class="del_appoint">  <i class="glyphicon glyphicon-trash"></i></a>'));		}	} else {		$results["data"][] =array(null,null,null,'No appointment Add',null,null,null,null);	}if($results != null) {	wp_send_json($results); // encode and send response} else { 	wp_send_json_error(); // {"success":false}}?>