<?phpglobal $wpdb;$appointment_category_details = $wpdb->get_results( "SELECT * from $wpdb->prefix"."apt_category");?><label for="service_type"><?php _e( 'Service', WL_A_P_SYSTEM ); ?></label><select  id="service_type" class="field form-control" name="service_type">	<option value="0">Select Service</option>	<?php foreach($appointment_category_details as $appointment_category_single_detail){ ?>		<optgroup label="<?php echo $appointment_category_single_detail->name; ?>">		<?php $service_table=	$wpdb->prefix."apt_services";		$appointment_details = $wpdb->get_results( "SELECT * from $service_table where category= '$appointment_category_single_detail->id'");  		foreach($appointment_details as $appointment_client_single_detail){	?>		<option value="<?php echo $appointment_client_single_detail->first_name; ?> <?php echo $appointment_client_single_detail->last_name; ?>"><?php echo $appointment_client_single_detail->first_name; ?> <?php echo $appointment_client_single_detail->last_name; ?></option>		<?php } ?>		</optgroup>	<?php } ?></select><span class="validation_alert" id="cal_service_alert"><?php _e("Please select one", WL_A_P_SYSTEM ); ?></span>