<?phpglobal $wpdb;$ap_fecth=$wpdb->get_results("select * from $wpdb->prefix"."apt_clients");$num_rows=count($ap_fecth);if($num_rows !==0) {	foreach($ap_fecth as $value) {		$id=$value->id;		$first_name=$value->first_name;		$last_name=$value->last_name;		$phone=$value->phone;		$skype_id =$value->skype_id ;		$email=$value->email ;		$notes =$value->notes ;		$results["data"][] =array('<input type="checkbox" name="check[]" id="my_check" class="checkbox1" value="'.$id.'"/>',$first_name,$last_name ,$phone,$skype_id,$email,$notes,array('<a style="margin-right: 5px;" href="#ab_update_customer_dialog"  data-backdrop="true" data-toggle="modal" data-id="'.$id.'"><i class="glyphicon glyphicon-pencil"></i></a> &nbsp <a href="#'.$id.'" class="del_id">  <i class="glyphicon glyphicon-trash"></i></a>'));	}} else {	$results["data"][] =array(null,null,null,__("No Customer Add","WL_A_P_SYSTEM"),null,null,null,null);}if($results != null) {	wp_send_json($results); // encode and send response} else {	wp_send_json_error(); // {"success":false}}?>